#!/usr/bin/env node
import { program } from 'commander';
import chalk from 'chalk';
import { initCommand } from '../src/commands/init.js';

console.log(chalk.bold.cyan('ðŸ”® XANDRIA CLI v1.0.0'));

program
  .name('xcli')
  .description('High-performance CLI tool generated by Xandria')
  .version('1.0.0');

program
  .command('init')
  .description('Initialize a new project')
  .action(initCommand);

program
  .command('info')
  .description('Show environment info')
  .action(() => {
    console.log(chalk.green('Environment: Production'));
    console.log(chalk.yellow(`Node Version: ${process.version}`));
  });

program.parse();